package com.explorejava.streams;import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;public class ConvertUppercaseUsingMap {    public static void main(String[] args) {        List<String> lowerCase = Arrays.asList("a", "b", "c");        List<String> upperCase = lowerCase.stream().map(String::toUpperCase).collect(Collectors.toList());        System.out.println(upperCase);    }}