package com.explorejava.streams;import com.explorejava.model.User;import com.explorejava.model.UserDTO;import java.util.ArrayList;import java.util.List;public class StreamMapCollectionExamples {    public static void main(String[] args) {        List<User> users = new ArrayList<User>();        users.add(new User(1, "Gokul", "secret", "gokul@xyz.com"));        users.add(new User(2, "Nakul", "secret", "nakul@xyz.com"));        users.add(new User(3, "Rakul", "secret", "rakul@xyz.com"));        users.add(new User(4, "Priya", "secret", "priya@xyz.com"));        List<UserDTO> userDTO = new ArrayList<UserDTO>();        users.stream().map((User user) -> new UserDTO(user.getId(), user.getUserName(), user.getEmail())).forEach(userDTO1 -> {            System.out.println(userDTO1);        });    }}